$((function(){$(".dropdown-button").on("change",(function(){const t=this.value.toLowerCase();$.ajax({method:"GET",url:`https://api.nytimes.com/svc/topstories/v2/${t}.json?api-key=ALcL70xVAiEjjiKsqBxMnAX2VdWNKIIK&limit=12`,dataType:"json"}).done((function(t){$(".news-span").empty(),$.each(t.results,(function(t,a){if(0!==a.multimedia.length){const t=`<div class='bg-img' style='background-image: url(${a.multimedia[4].url})'>\n                        <h3 class='article-title article-text'>${a.title}</h3>\n                        <p class='article-abstract article-text'>${a.abstract}</p>\n                    </div>`;$(".news-span").append(t)}}))}))})),$(".bg-img").on("hover",(function(){$(".article-abstract").classList.toggle("show")}))}));